<div class="author h-card bglight shadow pad mb">
<?php 	$display_name = get_the_author_meta( 'display_name' );	if ( empty( $display_name ) )		$display_name = get_the_author_meta( 'nickname' );	$user_avatar = esc_url( get_avatar_url( get_the_author_meta( 'user_email' ) , 90 ) ); 	$user_description = nl2br( get_the_author_meta( 'user_description' ) );	$user_posts = get_author_posts_url( get_the_author_meta( 'ID' ) );	$user_website = get_the_author_meta( 'url' );	?><h6>About The Author</h6> <img class="u-photo" alt="Photo of <?php echo $display_name; ?>"       src="<?php echo $user_avatar; ?>" /><h3 class="p-name"><?php echo $display_name; ?></h3><p class="p-note lead"><?php echo $user_description; ?></p><p class="author-footer"><?php if ( ! empty( $user_website ) ) {	echo'<a href="' . $user_website .'" class="u-url" target="_blank" rel="nofollow">Visit Author Website</a> | ';}?><a href="<?php echo $user_posts ?>">View All Posts</a></p>
</div>
